     Served Orders: [17, 8, 12, 19, 24, 2]

would be first-come, first-served.

Note - Order numbers are arbitrary. They do not have to be in increasing order. */

function isFirstComeFirstServed(takeOutOrders, dineInOrders, servedOrders) {
    var takeOutOrdersIndex = 0;
    var dineInOrdersIndex = 0;
    var takeOutOrdersMaxIndex = takeOutOrders.length - 1;
    var dineInOrdersMaxIndex = dineInOrders.length - 1;
    for (var i = 0; i < servedOrders.length; i++) {
        var order = servedOrders[i];
        // if we still have orders in takeOutOrders
        // and the current order in takeOutOrders is the same // as the current order in servedOrders
        if (takeOutOrdersIndex <= takeOutOrdersMaxIndex &&
            order === takeOutOrders[takeOutOrdersIndex]) {
            takeOutOrdersIndex++;
            // if we still have orders in dineInOrders
            // and the current order in dineInOrders is the same // as the current order in servedOrders
        } else if (dineInOrdersIndex <= dineInOrdersMaxIndex &&
            order === dineInOrders[dineInOrdersIndex]) {
            dineInOrdersIndex++;
            // if the current order in servedOrders doesn't match the current
            // order in takeOutOrders or dineInOrders, then we're not serving first-come, // first-served
        } else {
            return false;
        }
    }
    // check for any extra orders at the end of takeOutOrders or dineInOrders if (dineInOrdersIndex != dineInOrders.length ||
    takeOutOrdersIndex != takeOutOrders.length) {
    return false;
}
// all orders in servedOrders have been "accounted for" // so we're serving first-come, first-served!
return true;
}

function canTwoMoviesFillFlight(movieLengths, flightLength) {
    // Movie lengths we've seen so far 
    const movieLengthsSeen = new Set();
    for (let i = 0; i < movieLengths.length; i++) {
        const firstMovieLength = movieLengths[i];
        const matchingSecondMovieLength = flightLength - firstMovieLength;
        if (movieLengthsSeen.has(matchingSecondMovieLength)) {
            return true;
        }
        movieLengthsSeen.add(firstMovieLength);
    }
    // We never found a match, so return false
    return false;
}

