function getMaxProfit(stock_prices) {
    // Check if we have at least 2 prices
    if (stock_prices.length < 2) {
        throw new Error("At least two prices are required to calculate a profit.");
    }

    // Initialize the minimum price as the first stock price
    let minPrice = stock_prices[0];
    // Initialize max profit as the difference between the first two prices
    let maxProfit = stock_prices[1] - stock_prices[0];

    // Iterate through the list starting from the second price
    for (let i = 1; i < stock_prices.length; i++) {
        let currentPrice = stock_prices[i];

        // Calculate the potential profit from selling at the current price
        let potentialProfit = currentPrice - minPrice;

        // Update maxProfit if the current profit is greater than the previous max
        maxProfit = Math.max(maxProfit, potentialProfit);

        // Update minPrice if the current price is lower than the previous min
        minPrice = Math.min(minPrice, currentPrice);
    }

    return maxProfit;
}

