    function maxValue(arr){
        return arr.reduce((max, val) => max > val ? max : val);
    }

    let totalSeatRequired = P.reduce((s, p) => { return p+s;}, 0);
    let minCarNeeded = 0;
    while(totalSeatRequired > 0){
        let max = maxValue(S);
        let maxIdx = S.indexOf(max);
        minCarNeeded += 1;
        totalSeatRequired -= max;
        S.splice(maxIdx, 1);
