var findBuildings = function(heights) {    
    if(heights.length == 0) return [];

    let buildingIdx = [];
    let maxSoFar = 0;
    for(let i= (heights.length - 1); i >=0; i--){
        if(heights[i] > maxSoFar) {
            buildingIdx.push(i)
            maxSoFar = heights[i];
        };
    }

    return buildingIdx.reverse();
};

