const fn1 = (e) => { console.log(e.target.value); };

const debounce = (consFn, delay) => {
  let timeout;

  return function(){
    let context = this;
    let args = arguments;

    clearTimeout(timeout);
    timeout = setTimeout(() => {
      consFn.apply(context, args);
    }, delay);
  }
}

const throttle = (consFn, delay) => {
  let inThrottle = true;

  return function(){
    let context = this;
    let args = arguments;

    if(inThrottle){
      consFn.apply(context, args);
      inThrottle = false;
      setTimeout(() => {
        inThrottle = true;
      }, delay)
    }
  }
}

